mapper.select:

select id, order_id as orderId, stat, crt, upt
from order_process
where id=#{id}


select id, order_id as orderId, stat, crt, upt
from order_process
where order_id=#{orderId}


select id, order_id as orderId, stat, crt, upt
from order_process
where stat=#{stat}

mapper.update:

update order_process
set stat=#{stat},upt=now()
where order_id=#{orderId}
